//------------------------------------------------------------------------------------
var events = require("./pp-events.js")
//------------------------------------------------------------------------------------
var Event = new events()
//------------------------------------------------------------------------------------
var sayHello = function( msg ){
	console.log(msg)
}
//------------------------------------------------------------------------------------
function A(msg){
	console.log("Title: "+msg)
}
//------------------------------------------------------------------------------------
Event.on("sayHello",sayHello)
Event.on("sayHelloOtro",sayHello)
Event.on("sayHello",A)
//------------------------------------------------------------------------------------
Event.emit("sayHello","Custom Message")
//------------------------------------------------------------------------------------
